    public static int findTheFloor(String directions) {
        // start at the bottom
        int floor = 0;
        // haven't entered the basement yet
        boolean enteredBasement = false;
        int basementPosition = 0;
        // each time we encounter '(' we go up a floor, each time we encounter ')' we go down a floor
        for (int i = 0; i < directions.length(); i++) {
            if (directions.charAt(i) == '(') {
                floor++;
            } else {
                floor--;
            }
            // if this is the first time entering the basement (floor -1), mark position
            if (!enteredBasement && floor == -1) {
                basementPosition = i + 1;
                enteredBasement = true;
            }
        }
        return floor;
    }
