    public static int findTheFloor(String directions) {
        int floor = 0;
        boolean enteredBasement = false;
        int basementPosition = 0;
        for (int i = 0; i < directions.length(); i++) {
            if (directions.charAt(i) == '(') {
                floor++;
            } else {
                floor--;
            }
            if (!enteredBasement && floor == -1) {
                basementPosition = i + 1;
                enteredBasement = true;
            }
        }
        return floor;
    }
